import react,{useEffect,useState} from 'react'
import {useLogout} from '../hooks/useLogout'
import { useNavigate } from 'react-router-dom'
import { useAuthContext } from '../hooks/useAuthContext'
import { useReviewContext } from '../hooks/useReviewContext'
import {FaStar} from 'react-icons/fa'
import NavBar from '../items/NavBar'
import './Home.css'
const Home = () => {
    const {reviews,dispatch} = useReviewContext()
    const {user} = useAuthContext()
    const navigate = useNavigate()
    const {logout} = useLogout()



    useEffect(() => {
        const fetchReviews = async () => {
            const response = await fetch('http://localhost:8000/api/review',{

                headers:{

                    'Authorization' : `Bearer ${user.token}`
                }

            })
            const json = await response.json()

            if(response.ok){
                dispatch({type:'SET_REVIEW', payload:json})
            }
        }
        if(user ){
            fetchReviews()
        }

    },[dispatch,user])

    return(

        <div className='main-container'>
            <NavBar />
            {!user &&(
            <div className="nonloggedinuser">
                  Sign in to Read and Write Reviews!  
            </div>
            )}
            <div className='reviews' >
                {reviews && reviews.map((review)=>(
                <div className='review-content' >
                    <h2>Posted by: {review.name}</h2>
                    {
                    [...Array(review.rating)].map((index) => {

                    return(


                        <FaStar className='star' size={25} 
                        color={review.rating  <= review.rating ? "#ffc107":"#e4e5e9"}/>

                    )
                })}<div> {review.post} </div>

                   </div>
                ))}

            </div>
            {user &&(
           <div className='Create-Button'>
            <button onClick={()=> navigate("/create")}>Write a Review</button>
            </div>
            )}
        </div>
    )
}
export default Home
