import React, { useEffect, useState } from 'react'
import './NavBar.css';
import Button from './Button'
import { Link,useNavigate } from 'react-router-dom'
import { useAuthContext } from '../hooks/useAuthContext';
import { useLogout } from '../hooks/useLogout';
function NavBar() {
    const [click, setClick] = useState(false)
    const [button, setbutton] = useState(true)
    const { user } = useAuthContext()
    const closeMobileMenu = () => setClick(false)
    const navigate = useNavigate()
    const {logout} = useLogout()
    const handleSubmit = () => {
        if(user){
            logout()
        }else{
            navigate('/sign-up')
        }
    }
    const showButton = () => {
        if (window.innerWidth <= 960) {
          setButton(false);
        } else {
          setButton(true);
        }
      };

      useEffect(() => {
        showButton();
      }, []);
      window.addEventListener('resize', showButton);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to='/' className="navbar-logo">
                        Will's Cleaning Service
                    </Link>




                    <div className='menu-icon' onClick={handleSubmit}>
                        <i className={click ? 'fa fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <Link to='/about' className="nav-links" onClick={closeMobileMenu}>
                                About
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/services' className="nav-links" onClick={closeMobileMenu}>
                                Services
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/review' className="nav-links" onClick={closeMobileMenu}>
                                Reviews
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/sign-up' className="nav-links" onClick={closeMobileMenu}>
                                {user && (
                                    <div>
                                      <button className='btn--outline' onClick={handleSubmit}>Log Out</button>
                                    </div>
                                )}
                                                                {!user && (
                                    <div>
                                        <button className='btn--outline' onClick={handleSubmit}>Sign Up</button>
                                    </div>
                                )}

                            </Link>
                        </li>
                    </ul>


                </div>



            </nav>
        </>
    )
}

export default NavBar
