import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link, useNavigate } from 'react-router-dom'
import { useAuthContext } from '../hooks/useAuthContext'
const Create = () => {
    const {user} = useAuthContext()
    const [post, setPost] = useState("")
    const [rating, setRating] = useState("")
    const [email, setEmail] = useState()


    const [errors, setErrors] = useState([])
    const navigate = useNavigate({})
    const handleSubmit = (e) => {
        e.preventDefault()
        email = JSON.stringify(user.email)
        axios.post('http://localhost:8000/api/review', { post,rating,email })
            .then((res) => {
                console.log("This is our create page post", res)

            })
            .catch(err => {
                console.log("This a 400 error",err)
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr);
            })
    }

    return (
        <div>

            <hr />

            <form onSubmit={handleSubmit} >

                {errors.map((err, index) => <p key={index}>{err}</p>)}
                <hr />
                <label>Post:</label>


                <input type='text' value={post} onChange={(e) => setPost(e.target.value)} />

                <label>Rating:</label>


                <input type='number' value={rating} onChange={(e) => setRating(e.target.value)} />




                <input type='hidden' value={email} onChange={(e) => setEmail(e.target.checked)} />


                <button type="submit"> Submit </button>
                <button><Link to ={'/'}>Cancel</Link></button>
            </form>

        </div>
    )
}
export default Create
